// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

contract Counter {
    uint256 public count;

    constructor() {
        count = 0;
    }

    function inc() public {
        count += 1;
    }

    function incBy(uint256 _amount) public {
        count += _amount;
    }

    function dec() public {
        count -= 1;
    }

    function get() public view returns (uint256) {
        return count;
    }
}
